componentDidMount(){
  const CITY_ID = 23;
  const self = this;
  fetch('localhost:3000/rates/The Wall')
  .then(response => response.json())
  .then(data => {
    console.log(data)
    self.forceUpdate();
  })
}



  render(){ 
    return  (
    <div>
    <ReviewCard />
    </div>

      );
  }
}

export default AllReviews;
