import ReviewCard from './ReviewCard';
import Coment from './Coment';

class Review extends React.Component {

	constructor(){
		super();
		this.state = {
			cities: []
		}
	}

componentDidMount(){
	const self = this;
	fetch('http://localhost:3000/places')
	.then(response => response.json())
	.then(data => {this.state.cities = data;
		self.forceUpdate()})
}


  render()  {
  return (
  	<div>
  	<div className='container'>
  	<div className="col orange lighten-4 z-depth-1">
  	<div className="row ">
  		<div className='col s6'>
  			<h1 className='girlNextDoor'>{this.state.cities[1].city}</h1>
  			<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
  		</div>
  		<div className='col s6'>
  			<img alt='City' className='responsive-img'src="https://www.urbanarts.com.br/imagens/produtos/026863/0/Ampliada/paisagem.jpg" />    	    
  		</div>
  	</div>
  	</div>


  	<ReviewCard />

  	</div>


  	<footer className="page-footer orange lighten-3">
  		<Coment />	
      </footer> 



    </div>);
    };
}

export default Review;
